// src/actions/authActions.js
import axios from "axios";
import setAuthToken from "../utils/setAuthToken";

export const loginUser = (credentials, history) => (dispatch) => {
    axios
        .post("http://localhost:8000/api/auth/token/login/", credentials)
        .then((res) => {
            const { token, user } = res.data;
            dispatch({
                type: "LOGIN_SUCCESS",
                payload: { token, user },
            });
            setAuthToken(token);
            history.push("/");
        })
        .catch((err) => {
            dispatch({
                type: "AUTH_ERROR",
            });
        });
};

export const registerUser = (userData, history) => (dispatch) => {
    axios
        .post("/api/auth/register", userData)
        .then((res) => {
            const { token, user } = res.data;
            dispatch({
                type: "REGISTER_SUCCESS",
                payload: { token, user },
            });
            setAuthToken(token);
            history.push("/");
        })
        .catch((err) => {
            dispatch({
                type: "AUTH_ERROR",
            });
        });
};

export const logoutUser = () => (dispatch) => {
    dispatch({
        type: "LOGOUT",
    });
    setAuthToken(null);
};
